-- Save workload metrics to the database (SQLite)
-- Parameters: timestamp, tenant_id, user_id, request_id, duration_ms, context_size, 
--             memory_usage, cpu_usage, path, method, status_code, response_size, error_message
-- Note: ID is auto-generated by SQLite's AUTOINCREMENT

INSERT INTO workload_metrics (
    timestamp, tenant_id, user_id, request_id, duration_ms, context_size, 
    memory_usage, cpu_usage, path, method, status_code, response_size, error_message
) VALUES (
    ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?
);
